function updateFlex(){$(".flex").each(function(a,t){if($(t).data("gap")){var i=parseFloat($(t).data("gap"))/2+"px";$(t).hasClass("vertical")?($(t).children().css({"margin-top":i,"margin-bottom":i}),$(t).children().first().css("margin-top","0"),$(t).children().last().css("margin-bottom","0")):($(t).children().css({"margin-left":i,"margin-right":i}),$(t).children().first().css("margin-left","0"),$(t).children().last().css("margin-right","0"))}})}$(document).ready(function(){updateFlex()});